tinymce.PluginManager.add('dkfiles', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('dkimage', {
        icon: 'image',
        tooltip: 'Insertar imagen',
        onclick: function() {
            ventanaPopup("./filemanager/index.php?button=none&type=image");
        }
    });
    
    editor.addButton('dkvideo', {
        icon: 'media',
        tooltip: 'Insertar video',
        onclick: function() {
            ventanaPopup("./filemanager/index.php?button=none&type=video");
        }
    });
    
    editor.addButton('dkfile', {
        icon: 'newdocument',
        tooltip: 'Insertar archivo',
        onclick: function() {
            ventanaPopup("./filemanager/index.php?button=none&type=file");
        }
    });
});